import React, { Component, useState } from 'react';
// import { Table, Input, Button, Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
} from '../../../actions/chapterAction';
import '../Division/alldivision.css'
import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { Carousel } from 'react-carousel-minimal';
import Slider from 'react-touch-drag-slider';
import styled, { createGlobalStyle, css } from 'styled-components/macro'
import SliderShow2 from './slidertest';

const AppStyles = styled.main`
  height: 100vh;
  width: 100vw;
`

const GlobalStyles = createGlobalStyle`
  * {
    box-sizing: border-box;
  }
  html,body {
    padding: 0;
    margin: 0;
  }
`

const Button = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}`

class SliderShow extends Component {
  constructor(props){
    super(props);
    this.state={
      SlidetableLoading : false,
      index : 1
    //   slideData1 : this.props.chapter.slideData,
    //   carouselData : [
    //       {
            
    //       }
    //   ]
    }
  }

//   openModal = (id,mode)=>{
//     this.props.ChangeSlideModalState(true,id,mode);
//   }

//   closeModal = ()=>{
//     this.props.ChangeSlideModalState(false,null,'Регистрация');
//   }

  componentDidMount(){
    this.props.ChangeSlideTableData(this.props.id);
    // const [index, setIndex] = useState(1)
  }
  

    render() {
        
        // const data = this.state.data2;
        // var dada = this.props.chapter.slideData;
        // console.log('fdfd',data)
        // var data1 = [...this.state.carouselData];
        var data1=[{image: 'https://images.unsplash.com/photo-1609952048180-7b35ea6b083b?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80', caption: 'assas'},
        {image: 'https://images.unsplash.com/photo-1608241175281-722a1c6111be?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=634&q=80', caption: 'asa'}];

        // var data2=[];

        // this.props.chapter.slideTableData.map((d,i)=>{
            
        //     data2[i]=(
        //         {
        //         image: d.slimage,
        //         caption: d.slbody
        //         }
        //     )
        // })
        // console.log(data2);

        const increment = () => {
            if (this.state.index < data1.length - 1) this.setState({ index : this.state.index + 1})
        }

        const decrement = () => {
            if (this.state.index > 0) this.setState({ index : this.state.index - 1})
        }
        // const captionStyle = {
        //     fontSize: '2em',
        //     fontWeight: 'bold',
        // }
        // const slideNumberStyle = {
        //     fontSize: '20px',
        //     fontWeight: 'bold',
        // }

        // return (
        //     <div style={{ textAlign: "center" }}>
        //         <h2>React Carousel Minimal</h2>
        //         <p>Easy to use, responsive and customizable carousel component for React Projects.</p>
        //         {/* <div style={{
        //         padding: "0 20px"
        //         }}> */}
        //         <Carousel
        //         data={data2}
        //             time={2000}
        //             width="850px"
        //             height="500px"
        //             captionStyle={captionStyle}
        //             radius="10px"
        //             infiniteLoop={true}
        //             slideNumber={true}
        //             slideNumberStyle={slideNumberStyle}
        //             captionPosition="bottom"
        //             //automatic={true}
        //            // dots={true}
        //             pauseIconColor="white"
        //             pauseIconSize="40px"
        //             slideBackgroundColor="darkgrey"
        //             slideImageFit="cover"
        //             //thumbnails={true}
        //             thumbnailWidth="100px"
        //             style={{
        //             textAlign: "center",
        //             maxWidth: "850px",
        //             maxHeight: "500px",
        //             margin: "40px auto",
        //             }}
        //         >
        //             {/* <div>{data2}</div> */}
        //         </Carousel>
        //         {/* </div> */}
        //     </div>
        // );

        return (
            <AppStyles>
            <Button onClick={decrement} left disabled={this.state.index === 0}>
            〈
            </Button>
            <Button
            onClick={increment}
            right
            disabled={this.state.index === data1.length - 1}
            >
            〉
            </Button>
            <Slider
              onSlideComplete={(i) => {
                console.log('finished dragging, current slide is', i)
              }}
              onSlideStart={(i) => {
                console.log('started dragging on slide', i)
              }}
              activeIndex={0}
              threshHold={100}
              transition={0.5}
              scaleOnDrag={true}
            >
              {data1.map(({image,caption}, i) => (
                <img src={image} key={i} alt={caption} />
              ))}
            </Slider>
            </AppStyles>
            
      )
    }
}

const mapStateToProps = state => ({
    admin : state.admin,
    chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeSlideSearchText,
    ChangeSlideTableData,
    ChangeSlideModalState
})(SliderShow);
