import React, { Component, useEffect } from 'react'
import { connect } from 'react-redux';
// import { Table, Input,  Typography, Divider, Icon, Modal, Popconfirm } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState,
} from '../../../actions/trainerAction';
import { 
  ChangeHistorySearchText,
  ChangeHistoryTableData,
  ChangeHistoryModalState,
} from '../../../actions/chapterAction';

import Alert from '../../../components/common/alert';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
// import Test2 from './test2';
// import Test3 from './test3';
import styled from "styled-components";
// import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import classnames from 'classnames';
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

const Button = styled.button`
  font-size: 1.2rem;
  font-weight: bold;
  width: 40%;
  height: 3rem;
  // color: #fff;
  border: none;
  // background: #7159c1;
  border-radius: 5px;
  box-shadow: 0px 3px 15px 0px #666;
`;

const ButtonScr = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  // border-radius: 7.5px;
  border: 0.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

const Error = styled.div`
  top: 22rem;
  color: red;
  position: absolute;
  font-weight: bold;
`;

class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        questions:[],
        screen: true,
        count: 0,
        clickedLabel: {},
        answerLabel:{},
        answer: 0,
        error:false,
        screenFinish:false,
        second:60,
        time:{},
        checked:false,
        temp:[],
        t:0,
        optionDetails:[{
          quesid: null,
          chans: null,
          isAnswer :false
        }]
      }
    }
  
    componentDidMount(){
      this.props.ChangeQuestionTableData(this.props.id);
      this.props.ChangeHistoryTableData(this.props.id);
      console.log('CHID',this.props.chid);
      var questions=[];
      this.props.trainer.QuestionTableData.map((d,i)=>{
        questions[i]=(
          {
            question : d.body,
            // questionImage : d.quesimg,
            answerOptions: [
              { answerText: d.answer1, UserAnswer: d.userAnswer1 },
              { answerText: d.answer2, UserAnswer: d.userAnswer2 },
              { answerText: d.answer3, UserAnswer: d.userAnswer3 },
              { answerText: d.answer4, UserAnswer: d.userAnswer4 },
              { answerText: d.answer5, UserAnswer: d.userAnswer5 }
            ]
          }
        )
      });
      this.setState({
        questions:questions
      })
    }

    handleNextClick=(c)=>{
      console.log("handleNextClick");
      const { count, questions, clickedLabel, answer} = this.state;
      // console.log("Ques", questions);
      // var var1 = questions[c].question;
      // var1 = var1+'fg';
      // console.log(var1);
      // questions[c].question=var1;
        this.setState({
          // questions:questions,
          count: count+1,
          checked:false,
        });
    }

    handlePrevClick=()=>{
      console.log("handlePrevClick");
      const { count, questions, clickedLabel, answer} = this.state;
      if (questions.length >= count && count>0) {
        this.setState({
          count: count - 1,
          checked:false,
        });
      } 
    }

    handleClick = (c)=> {
      console.log("handleClick");
      const { count, questions, clickedLabel, answer, checked, quesid, chans} = this.state;
      console.log("Ques", questions);
      // var var1 = questions[c].question;
      // var1 = var1+'fg';
      // console.log(var1);
      questions[c].question=questions[c].question+'fg';
      if (questions.length >= count && clickedLabel.answerText) {
        this.setState({
          questions:questions,
          count: c,
          checked:false,
        });
      }
    }

    renderInitial = () => {
      console.log("renderInitial");
      const { second, count,questions } = this.state;
      this.setState({
        screen: false,
        count: 1
      });

      SecurePost({
        url : `${apis.CREATE_HISTORY}`,
        data : {
            chapterid : this.props.chid,
            materialid : this.props.id,
            startDate : Date().toLocaleString(),
            complationDate : Date().toLocaleString()+3,
            startTest: true,
            endTest: false,
            result: count
          }
        })
        .then((response)=>{
