export default function Result(props) {
    const [result, setResult] = useState(0);
    useEffect(()=>{
        console.log("1 - props",props.answerDetails[0].chosenOption);
        //var questions1=[];
        var nota = [];
        var noua = [];
        var s = props.answerDetails[0].chosenOption.length;
        props.answerDetails[0].chosenOption.map((d,i)=>{
            nota[i] = (d.options[0].isAnswer + d.options[1].isAnswer + d.options[2].isAnswer + d.options[3].isAnswer + d.options[4].isAnswer)
            noua[i] = ((d.options[0].userAnswer && d.options[0].isAnswer) + 
                       (d.options[1].userAnswer && d.options[1].isAnswer) + 
                       (d.options[2].userAnswer && d.options[2].isAnswer) + 
                       (d.options[3].userAnswer && d.options[3].isAnswer) + 
                       (d.options[4].userAnswer && d.options[4].isAnswer))
                // questions1[i]=(
                //     {
                //         _id: d._id,
                //         question : d.questionText,
                //         isOneTrueAnswer : (d.options[0].isAnswer + d.options[1].isAnswer + d.options[2].isAnswer + d.options[3].isAnswer 
                //             + d.options[4].isAnswer)===1 ? true : false,
                //         answerOptions: [
                //             { answerText: d.options[0].answers, UserAnswer: d.options[0].userAnswer, correctAnswer: d.options[0].isAnswer },
                //             { answerText: d.options[1].answers, UserAnswer: d.options[1].userAnswer, correctAnswer: d.options[1].isAnswer },
                //             { answerText: d.options[2].answers, UserAnswer: d.options[2].userAnswer, correctAnswer: d.options[2].isAnswer },
                //             { answerText: d.options[3].answers, UserAnswer: d.options[3].userAnswer, correctAnswer: d.options[3].isAnswer },
                //             { answerText: d.options[4].answers, UserAnswer: d.options[4].userAnswer, correctAnswer: d.options[4].isAnswer }
                //         ]
                //     }
                // )
            })
        console.log("Number of the true answer", nota)
        console.log("Number of the user answer", noua)
        var a=0;
        props.answerDetails[0].chosenOption.map((d,i)=>{
            a+=(noua[i] / nota[i]) * 100;
        })
        setResult(a/s);
    }, [])
    return(
        
        <div>
            {console.log(props.answerDetails[0].chosenOption)}
            
            <h3>Результаты тестирование</h3>
            <h3>Количество вопросов: {props.answerDetails[0].chosenOption.length}</h3>
            <h3>Процент правильных ответов: {this.state.result}</h3>
        </div>
        
    )
}
