import React, {useState, useEffect} from 'react';
import { connect } from 'react-redux';
import { Badge, Descriptions } from 'antd';
import { 
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState
} from '../../../actions/adminAction';

function CompanyList(props) {
    useEffect(()=>{
        props.ChangeCompanyTableData();
    }, [])

    var d = props.admin.companyTableData.filter(k=>k._id === props.userDetails.company);
    var data = d.length!==0 ? d[0] : null
    
    return(

        <Descriptions title="Информация о компаний" bordered>
            {console.log(data)}
            <Descriptions.Item label="Название">{!data ? null : data.name}</Descriptions.Item>
            <Descriptions.Item label="Логотип">{!data ? null : <img className="display" src={data.logo} style={{height: "60px"}} alt=""/>}</Descriptions.Item>
            {/* <Descriptions.Item label="Automatic Renewal">YES</Descriptions.Item> */}
            <Descriptions.Item label="Дата начала действия лицензии">2018-04-24 18:00:00</Descriptions.Item>
            <Descriptions.Item label="Дата окончания действия лицензии" span={4}>
            2019-04-24 18:00:00
            </Descriptions.Item>
            <Descriptions.Item label="Контактное лицо" span={4}>
            {/* <Badge status="processing" text="Running" /> */}
            </Descriptions.Item>
            <Descriptions.Item label="Количество лицензированных пользователей">$80.00</Descriptions.Item>
            <Descriptions.Item label="Количество использованных учетных записей в рамках лицензии">$20.00</Descriptions.Item>
            {/* <Descriptions.Item label="Official Receipts">$60.00</Descriptions.Item> */}
            <Descriptions.Item label="Описание">
            Data disk type: MongoDB
            <br />
            Database version: 3.4
            <br />
            Package: dds.mongo.mid
            <br />
            Storage space: 10 GB
            <br />
            Replication factor: 3
            <br />
            Region: East China 1<br />
            </Descriptions.Item>
        </Descriptions>
        
    )
}

const mapStateToProps = state => ({
    admin : state.admin
});

export default connect(mapStateToProps,{
    ChangeCompanySearchText,
    ChangeCompanyTableData,
    ChangeCompanyModalState
})(CompanyList);
