import React, { Component, useState } from 'react'
import Slider from 'react-touch-drag-slider'
import { connect } from 'react-redux';
import styled, { createGlobalStyle, css } from 'styled-components/macro'


const GlobalStyles = createGlobalStyle`
  * {
    box-sizing: border-box;
  }
  html,body {
    padding: 0;
    margin: 0;
  }
`
const AppStyles = styled.main`
  height: 82vh;
  width: 82vw;
`

const Button = styled.button`
  font-size: 2rem;
  z-index: 10;
  position: fixed;
  top: 50%;
  ${(props) =>
    props.right
      ? css`
          right: 0.5rem;
        `
      : css`
          left: 0.5rem;
        `}
`

export default function Test2(props) {
    const [index, setIndex] = useState(0)
          var questions=[];
          props.details.map((d,i)=>{
              
              questions[i]=(
                  {
                      questionText : d.body,
                      answerOptions: [
                        { answerText: d.answer1, isCorrect: d.trueans1 },
                        { answerText: d.answer2, isCorrect: d.trueans2 },
                        { answerText: d.answer3, isCorrect: d.trueans3 },
                        { answerText: d.answer4, isCorrect: d.trueans4 },
                        { answerText: d.answer5, isCorrect: d.trueans5}
                      ]
                  }
              )
              
          })
          console.log('pot:',questions.length)
          // console.log(questions);
          const increment = () => {
              if (index < questions.length - 1) setIndex(index + 1)
          }
  
          const decrement = () => {
              if (index > 0) setIndex(index - 1)
          }
  
    return (
      <>
        <GlobalStyles />
        <AppStyles> 
         <Button onClick={decrement} left disabled={index === 0}>
            〈
          </Button>
          <Button
            onClick={increment}
            right
            disabled={index === questions.length - 1}
          >
            〉
          </Button>
          {/* <Slider
            onSlideComplete={setIndex}
            onSlideStart={(i) => {
              console.log('started dragging on slide', i)
            }}
            activeIndex={index}
            threshHold={100}
            transition={0.3}
            scaleOnDrag={true}
          > */}
        <div>{questions.map((d, i) => (
            d.questionText
          ))}</div>
          {/* </Slider> */}
        </AppStyles>
       </>
    )
  }
