          count: c+1,
          clickedLabel: {},
          answer: clickedLabel.correctAnswer ? answer + 1 : answer,
          error: false,
          checked:false,
        });
      }
    }

    const handleEndClick = (c)=> {
    //   const { count, questions, clickedLabel } = this.state;
      if (questions.length === count) {
        this.setState({
          screenFinish: true,
          testEnded:true,
          testStarted:false,
        });
      }
    }

    const handleOptions = (answerOptions,c) => {
    //   const { clickedLabel,questions } = this.state;
      return answerOptions.map((a, i) => {
        return (
          <Label
            key={i}
            onClick={() =>{
                setQuestions(questions);
                setClickedLabel(a)
            //   this.setState({
            //     questions:questions,
            //     clickedLabel: a,
            //     error: false
            //   })
            }
            }
          >
            { questions[c].isOneTrueAnswer===false ?
            <Radio 
              checked={a.UserAnswer} 
              onChange={e => this.handleChange(e,answerOptions, c,i)}
            />
            :
            <Radio  
              checked={a.UserAnswer}
              onChange={e => this.handleChange2(e,answerOptions, c,i)}
            />
            }
            <Paragraph>{a.answerText}</Paragraph>
          </Label>
          
        );
      });
    };

    const handleChange = (e,answerOptions, c, i) => {
      const { questions } = this.state
        questions[c].answerOptions[i].UserAnswer=true;
        
        this.setState({
          questions:questions,
        });
    };

    const handleChange2 = (e,answerOptions, c, i) => {
      const { questions } = this.state
      questions[c].answerOptions[i].UserAnswer=true;

      if(questions[c].answerOptions[i].UserAnswer===true){
        answerOptions.map((d,j) =>{
          if(i!==j){
            questions[c].answerOptions[j].UserAnswer=false;
          }
        })
      }
        this.setState({
          questions:questions,
        });
    };

    const renderQuestions = () => {
    //   const { count,questions } = this.state;
      const NewQuestions = Array.from(questions);
      const removeQuestions = NewQuestions.slice(count - 1, count);
      return removeQuestions.map((q, i) => (

        <Container key={i}>
          <ScreenQuiz>
            <Counter/>

            <h3>{count}/{questions.length}</h3>
            <Title>{q.question}</Title>         
            <label>{count}</label>
            <ContainerLabel>
              {this.handleOptions(q.answerOptions, count-1)}
            </ContainerLabel>
            <Button onClick={()=>this.handleClick(count-1)}
                          style={{
                            fontSize: "1.2rem", 
                            fontWeight: "bold",
                            width: "40%",
                            height: "3rem",
                            border: "none",
                            borderRadius: "5px",
                            boxShadow: "0px 3px 3px 0px #666"
                          }}
            >
                Сохранить
            </Button>
            { count===questions.length ?
            <Button onClick={this.handleEndClick}
              style={{
                marginTop: '30px',
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
            >
                Завершить тест
            </Button>
              : null
          }
            <ButtonScr onClick={()=>this.handleNextClick(count)} right disabled={count === questions.length}>Следующий</ButtonScr>
            <ButtonScr onClick={this.handlePrevClick} left disabled={count===1}>Предыдущий</ButtonScr>
          </ScreenQuiz>
        </Container>

      ));
    };
  
    const renderResult = () => {
    //   const { answer,questions, screenFinish } = this.state;
  
      return (
        <Container>
          <ScreenQuiz screen={screenFinish}>
            <Title>Вы правильно ответили {answer} из {questions.length} вопросов</Title>
            <Button
              onClick={() =>
                this.setState({
                  count: 0,
                  screenFinish: false,
                  screen: true,
                  answer: 0
                })
              }

              style={{
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
            >
              Тестировать заново
            </Button>
          </ScreenQuiz>
        </Container>
      );
    };

    console.log("1 - props",props.q[0]);
    var questions1=[];
    props.q[0].chosenOption.map((d,i)=>{
        questions1[i]=(
                {
                  question : d.questionText,
                  isOneTrueAnswer : (d.options[0].isAnswer + d.options[1].isAnswer + d.options[2].isAnswer + d.options[3].isAnswer 
                    + d.options[4].isAnswer)===1 ? true : false,
                  // questionImage : d.quesimg,
                  answerOptions: [
                    { answerText: d.options[0].answers, UserAnswer: d.options[0].userAnswer, correctAnswer: d.options[0].isAnswer },
                    { answerText: d.options[1].answers, UserAnswer: d.options[1].userAnswer, correctAnswer: d.options[1].isAnswer },
                    { answerText: d.options[2].answers, UserAnswer: d.options[2].userAnswer, correctAnswer: d.options[2].isAnswer },
                    { answerText: d.options[3].answers, UserAnswer: d.options[3].userAnswer, correctAnswer: d.options[3].isAnswer },
                    { answerText: d.options[4].answers, UserAnswer: d.options[4].userAnswer, correctAnswer: d.options[4].isAnswer }
                  ]
                }
              )
        })
    console.log("2 - questions",questions1);

    return(
        renderQuestions(questions1)
    )
    // render() {
    //   const { screenFinish, questions, screen } = this.state;

    //   return !screenFinish && questions ? (
    //     <>{screen ? this.renderScreenInitial() : this.renderQuestions()}</>
    //   ) : (
    //     this.renderResult()
    //   );
    // }
  }
  
