import React, { Component, useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
    ChangeQuestionSearchText,
    ChangeQuestionTableData,
    ChangeQuestionModalState,
} from '../../../actions/trainerAction';
import { 
  ChangeTestSearchText,
  ChangeTestTableData,
  ChangeTestModalState,
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
  // ChangeAnswersModalState,
} from '../../../actions/chapterAction';

import Alert from '../../../components/common/alert';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
// import Test2 from './test2';
// import Test3 from './test3';
import styled from "styled-components";
// import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

// const Error = styled.div`
//   top: 22rem;
//   color: red;
//   position: absolute;
//   font-weight: bold;
// `;


class Test extends Component {
    constructor(props){
      super(props);
      this.state={
        questions:[],
        loading:false,
        screen: true,
        count: 0,
        clickedLabel: [],
        answer: 0,
        error:false,
        screenFinish:false,
        second:60,
        time:{},
        checked:false,
        testStarted:false,
        testEnded:false,
        testDidNotStart:true,
        details:[],
      }
    }
  
    componentWillMount(){
      console.log("1 - componentDidMount")
      this.props.ChangeTestTableData(this.props.id);
      this.props.ChangeAnswersTableData(this.props.id);


      
      console.log()
      var questions=[];
      if(this.state.details.length === 0){
        return Alert('warning',"Сперва пройдите материалы")
      }
      else{
        this.state.details.chosenOption.map((d,i)=>{
          questions[i]=(
                {
                  question : d.questionText,
                  isOneTrueAnswer : (d.options[0].isAnswer + d.options[1].isAnswer + d.options[2].isAnswer + d.options[3].isAnswer 
                    + d.options[4].isAnswer)===1 ? true : false,
                  // questionImage : d.quesimg,
                  answerOptions: [
                    { answerText: d.options[0].answers, UserAnswer: d.options[0].userAnswer, correctAnswer: d.options[0].isAnswer },
                    { answerText: d.options[1].answers, UserAnswer: d.options[1].userAnswer, correctAnswer: d.options[1].isAnswer },
                    { answerText: d.options[2].answers, UserAnswer: d.options[2].userAnswer, correctAnswer: d.options[2].isAnswer },
                    { answerText: d.options[3].answers, UserAnswer: d.options[3].userAnswer, correctAnswer: d.options[3].isAnswer },
                    { answerText: d.options[4].answers, UserAnswer: d.options[4].userAnswer, correctAnswer: d.options[4].isAnswer }
                  ]
                }
              )
        })
      }
      console.log("1 - End cDM")
      this.setState({
        questions:questions,
        loading:true
      })
    }

    handleNextClick=(c)=>{
      const { count} = this.state;
        this.setState({
          count: count+1,
          checked:false,
        });
    }

    handlePrevClick=()=>{
      const { count, questions} = this.state;
      if (questions.length >= count && count>0) {
        this.setState({
          count: count - 1,
          checked:false,
        });
      } 
    }

    handleClick = (c)=> {
      const { count, questions, clickedLabel, answer} = this.state;
      // questions[c].question=questions[c].question+'fg';
      if (questions.length >= count && clickedLabel.answerText) {
        this.setState({
          questions:questions,
          count: c+1,
          clickedLabel: {},
          answer: clickedLabel.correctAnswer ? answer + 1 : answer,
          error: false,
          checked:false,
        });
      }
    }

    handleEndClick = (c)=> {
      const { count, questions, clickedLabel } = this.state;
      if (questions.length === count) {
        this.setState({
          screenFinish: true,
          testEnded:true,
          testStarted:false,
        });
      }


    }

    renderInitial = () => {
      console.log("renderInitial")
      const { count } = this.state;

      this.setState({
        screen: false,
        // loading:false,
        count: 1,
        // testStarted:true,
        // testEnded:false,
        // testDidNotStart:false,
      });

      console.log("1  CREATE_TEST")
      SecurePost({
        url : `${apis.CREATE_TEST}`,
        data : {
          _id: this.props.chapter.testid,
            chapterid : this.props.chid,
            materialid : this.props.id,
            startDate : Date().toLocaleString(),
            complationDate : Date().toLocaleString(),
            startTest: true,
            endTest: false,
            result: 0
          }
        })
        .then((response)=>{
          if(response.data.success){
              this.props.ChangeTestModalState(false,null, 'Save'); 
              Alert('success','Успешно',response.data.message);
          }
          else{
              this.props.ChangeTestModalState(false,null, 'Save');
              return Alert('warning','Предупреждение!',response.data.message);
          }
        })
        .catch((error)=>{
            this.props.ChangeTestModalState(false,null, 'Save');
            return Alert('error','Ошибка!','Ошибка сервера');
        })
    
        
      SecurePost({
        url : `${apis.CREATE_TST}`,
        data : {
          materialId : this.props.id,
          testid : this.props.chapter.testid,
        }
      })
    };

    renderScreenInitial = () => {
      return (
        <Container>
          <ScreenQuiz screen={this.state.screen}>
            <Title>Тестирование</Title>
            <Button 
              onClick={this.renderInitial} 
              style={{
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
              >
                Начать тест
            </Button>
          </ScreenQuiz>
        </Container>
      );
    };




    render() {
      const { screenFinish, questions, screen } = this.state;

      return !screenFinish && questions ? (
        <>{screen ? this.renderScreenInitial() : <TestPage q={this.props.chapter.AnswerTableData}/>}</>
      ) : (
        this.renderResult()
      );
    }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeQuestionSearchText,
      ChangeQuestionTableData,
      ChangeQuestionModalState,
      ChangeTestSearchText,
      ChangeTestTableData,
      ChangeTestModalState,
      ChangeAnswersSearchText,
      ChangeAnswersTableData,
      // ChangeAnswersModalState
  })(Test);
