import React, { Component, useState, useEffect } from 'react'
import { connect } from 'react-redux';
import { Button } from 'antd';
// import Highlighter from 'react-highlight-words';
import  { css } from 'styled-components/macro'
import { 
  ChangeAnswersSearchText,
  ChangeAnswersTableData,
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import { SecurePost, SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
import Counter from './Counter';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 80vh;
  width: 100%;
  /*background: #7159c1;*/
`;

const ScreenQuiz = styled.div`
  display: flex;
  justify-content: ${props => (props.screen ? "space-between" : "center")};
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  padding: ${props => (props.screen ? "5rem" : ".5rem")};
  width: 100%;
  height: 80vh;
  background: #fff;
  border-radius: 5px;
  box-shadow: 0px 5px 40px -20px #000;

`;

const Title = styled.h1`
  font-size: 2.5rem;
  // color: #7159c1;
  margin-bottom: 0.5rem;
  text-align: center;
  max-width: 90%;
`;

const Paragraph = styled.p`
  font-size: 1.5rem;
`;

// const Button = styled.button`
//   font-size: 1.2rem;
//   font-weight: bold;
//   width: 40%;
//   height: 3rem;
//   // color: #fff;
//   border: none;
//   // background: #7159c1;
//   border-radius: 5px;
//   box-shadow: 0px 3px 3px 0px #666;

//   @-webkit-keyframes pulsate {
//     0% {transform: scale(0.1, 0.1); opacity: 0.0;}
//     50% {opacity: 1.0;}
//     100% {transform: scale(7, 7); opacity: 0.0;}
// }
// `;

const ButtonScr = styled.button`
  font-size: 1.4rem;
  z-index: 10;
  position: fixed;
  top: 15%;
  ${(props) =>
    props.right
      ? css`
          right: 25rem;
        `
      : css`
          left: 25rem;
        `}
`

const ContainerLabel = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 auto 0.5rem auto;
`;

const Label = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Radio = styled.input.attrs({ type: 'checkbox' })`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15px;
  height: 15px;
  border-radius: 50%;
  border: 7.5px solid black;
  margin: 0 0.5rem 0 0;
  background: ${props => (props.clickedLabel ? "#7159c1" : "#fff")};
`;

// const IntoRadio = styled.div`
//   width: 5px;
//   height: 5px;
//   border-radius: 3px;
//   background: #fff;
// `;

// const Error = styled.div`
//   top: 22rem;
//   color: red;
//   position: absolute;
//   font-weight: bold;
// `;


class TestPage extends Component {
    constructor(props){
      super(props);
      this.state={
        questions:[],
        loading:false,
        screen: true,
        count: 0,
        clickedLabel: [],
        answer: 0,
        error:false,
        screenFinish:false,
        second:60,
        time:{},
        checked:false,
        testStarted:false,
        testEnded:false,
        testDidNotStart:true,
        details:[],
      }
    }
  
    // componentDidMount(){
    //   console.log("1 - componentDidMount")
    //   this.props.ChangeAnswersTableData(this.props.id);
    
    //   console.log("FFFFFFFFFFFFFFFFFFFFFFFFFFFFFF", this.props.chapter.AnswerTableData[0])
    //   var questions=[];
    //   if(this.props.chapter.AnswerTableData.length === 0){
    //     return Alert('warning',"Сперва пройдите материалы")
    //   }
    //   else{
    //     this.props.chapter.AnswerTableData[0].chosenOption.map((d,i)=>{
    //       questions[i]=(
    //             {
    //               question : d.questionText,
    //               isOneTrueAnswer : (d.options[0].isAnswer + d.options[1].isAnswer + d.options[2].isAnswer + d.options[3].isAnswer 
    //                 + d.options[4].isAnswer)===1 ? true : false,
    //               // questionImage : d.quesimg,
    //               answerOptions: [
    //                 { answerText: d.options[0].answers, UserAnswer: d.options[0].userAnswer, correctAnswer: d.options[0].isAnswer },
    //                 { answerText: d.options[1].answers, UserAnswer: d.options[1].userAnswer, correctAnswer: d.options[1].isAnswer },
    //                 { answerText: d.options[2].answers, UserAnswer: d.options[2].userAnswer, correctAnswer: d.options[2].isAnswer },
    //                 { answerText: d.options[3].answers, UserAnswer: d.options[3].userAnswer, correctAnswer: d.options[3].isAnswer },
    //                 { answerText: d.options[4].answers, UserAnswer: d.options[4].userAnswer, correctAnswer: d.options[4].isAnswer }
    //               ]
    //             }
    //           )
    //     })
    //   }
    //   console.log("1 - End cDM")
    //   this.setState({
    //     questions:questions,
    //     loading:true
    //   })
    // }

    // handleNextClick=(c)=>{
    //   const { count} = this.state;
    //     this.setState({
    //       count: count+1,
    //       checked:false,
    //     });
    // }

    // handlePrevClick=()=>{
    //   const { count, questions} = this.state;
    //   if (questions.length >= count && count>0) {
    //     this.setState({
    //       count: count - 1,
    //       checked:false,
    //     });
    //   } 
    // }

    handleClick = (c)=> {
      const { count, questions, clickedLabel, answer} = this.state;
      // questions[c].question=questions[c].question+'fg';
      if (questions.length >= count && clickedLabel.answerText) {
        this.setState({
          questions:questions,
          count: c+1,
          clickedLabel: {},
          answer: clickedLabel.correctAnswer ? answer + 1 : answer,
          error: false,
          checked:false,
        });
      }
    }

    handleEndClick = (c)=> {
      const { count, questions, clickedLabel } = this.state;
      if (questions.length === count) {
        this.setState({
          screenFinish: true,
          testEnded:true,
          testStarted:false,
        });
      }
    }

    handleOptions = (answerOptions,c) => {
      const { clickedLabel,questions } = this.state;
      return answerOptions.map((a, i) => {
        return (
          <Label
            key={i}
            onClick={() =>{
              this.setState({
                questions:questions,
                clickedLabel: a,
                error: false
              })}
            }
          >
            { questions[c].isOneTrueAnswer===false ?
            <Radio 
              checked={a.UserAnswer} 
              onChange={e => this.handleChange(e,answerOptions, c,i)}
            />
            :
            <Radio  
              checked={a.UserAnswer}
              onChange={e => this.handleChange2(e,answerOptions, c,i)}
            />
            }
            <Paragraph>{a.answerText}</Paragraph>
          </Label>
          
        );
      });
    };

    handleChange = (e,answerOptions, c, i) => {
      const { questions } = this.state
        questions[c].answerOptions[i].UserAnswer=true;
        
        this.setState({
          questions:questions,
        });
    };

    handleChange2 = (e,answerOptions, c, i) => {
      const { questions } = this.state
      questions[c].answerOptions[i].UserAnswer=true;

      if(questions[c].answerOptions[i].UserAnswer===true){
        answerOptions.map((d,j) =>{
          if(i!==j){
            questions[c].answerOptions[j].UserAnswer=false;
          }
        })
      }
        this.setState({
          questions:questions,
        });
    };

    renderQuestions = () => {
      const { count,questions } = this.state;
      const NewQuestions = Array.from(questions);
      const removeQuestions = NewQuestions.slice(count - 1, count);
      return removeQuestions.map((q, i) => (

        <Container key={i}>
          <ScreenQuiz>
            <Counter/>

            <h3>{count}/{questions.length}</h3>
            <Title>{q.question}</Title>         
            <label>{count}</label>
            <ContainerLabel>
              {this.handleOptions(q.answerOptions, count-1)}
            </ContainerLabel>
            <Button onClick={()=>this.handleClick(count-1)}
                          style={{
                            fontSize: "1.2rem", 
                            fontWeight: "bold",
                            width: "40%",
                            height: "3rem",
                            border: "none",
                            borderRadius: "5px",
                            boxShadow: "0px 3px 3px 0px #666"
                          }}
            >
                Сохранить
            </Button>
            { count===questions.length ?
            <Button onClick={this.handleEndClick}
              style={{
                marginTop: '30px',
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
            >
                Завершить тест
            </Button>
              : null
          }
            <ButtonScr onClick={()=>this.handleNextClick(count)} right disabled={count === questions.length}>Следующий</ButtonScr>
            <ButtonScr onClick={this.handlePrevClick} left disabled={count===1}>Предыдущий</ButtonScr>
          </ScreenQuiz>
        </Container>

      ));
    };
  
    renderResult = () => {
      const { answer,questions, screenFinish } = this.state;
  
      return (
        <Container>
          <ScreenQuiz screen={screenFinish}>
            <Title>Вы правильно ответили {answer} из {questions.length} вопросов</Title>
            <Button
              onClick={() =>
                this.setState({
                  count: 0,
                  screenFinish: false,
                  screen: true,
                  answer: 0
                })
              }

              style={{
                fontSize: "1.2rem", 
                fontWeight: "bold",
                width: "40%",
                height: "3rem",
                border: "none",
                borderRadius: "5px",
                boxShadow: "0px 3px 3px 0px #666"
              }}
            >
              Тестировать заново
            </Button>
          </ScreenQuiz>
        </Container>
      );
    };

    // render() {
    //   const { screenFinish, questions, screen } = this.state;

    //   return !screenFinish && questions ? (
    //     <>{screen ? this.renderScreenInitial() : this.renderQuestions()}</>
    //   ) : (
    //     this.renderResult()
    //   );
    // }
  }
  
  const mapStateToProps = state => ({
      chapter : state.chapter,
      trainer : state.trainer
  });
  
  export default connect(mapStateToProps,{
      ChangeAnswersSearchText,
      ChangeAnswersTableData
  })(TestPage);
