import React, { Component } from 'react'
import './newdivision.css';
import {
    Form,
    Input,
    Button,
    Select
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeDivisionConfirmDirty,
    ChangeDivisionModalState,
    ChangeDivisionTableData
} from '../../../actions/adminAction';
import Alert from '../../../components/common/alert';
const { Option } = Select;
class NewDivision extends Component {


    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_DIVISION}`,
                        data : {
                            _id : this.props.admin.divisionId,
                            name : values.name,                            
                            description : values.description,
                            company : values.company
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeDivisionModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeDivisionTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeDivisionModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeDivisionModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div className="register-division-form">
                <div className="register-division-form-body">
                    <Form  onSubmit={this.handleSubmit}>
                        <Form.Item label="Название" hasFeedback className="input-admin-division">
                            {getFieldDecorator('name', {
                                initialValue : this.props.admin.divisiondetails.name,
                                rules: [{ required: true, message: 'Пожалуйста, введите название подразделения!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                    
                        <Form.Item label="Описание" hasFeedback className="input-admin-division">
                            {getFieldDecorator('description', {
                                initialValue : this.props.admin.divisiondetails.description,
                                rules: [
                                    {
                                        required: false,
                                        message: 'Описание!',
                                        whitespace: true
                                    },
                                ],
                            })(<Input />)}
                        </Form.Item> 

                        <Form.Item label="Компания" hasFeedback className="input-admin-division">
                            {getFieldDecorator('company', {
                                initialValue : this.props.admin.customerdetails.company,
                                rules: [{ required: false, message: 'Пожалуйста, введите название компании!', whitespace: true }],
                            })(
                            <Select 
                                showSearch
                                style = {{width:'100%'}}
                                placeholder="Выберите компанию"
                                optionFilterProp= "s"
                            >
                                {
                                    this.props.admin.companyTableData.map((d,i)=><Option key={d._id} s={d.name} value={d._id}>{d.name}</Option>)
                                }

                            </Select>
                            )}
                        </Form.Item>
                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.admin.Divisionmode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    admin : state.admin
});

const NewDivisionForm = Form.create({ name: 'register' })(NewDivision);

export default connect(mapStateToProps,{
    ChangeDivisionConfirmDirty,
    ChangeDivisionModalState,
    ChangeDivisionTableData
})(NewDivisionForm);
