import Var4Slides from './var4slides';
import Var3Questions from './var3questions';
const { TabPane } = Tabs;

export default class Var5Details extends Component {
    constructor(props){
        super(props);
        this.state={
            loading : true,
            details:null
        }
    }

    tabChange = (key)=>{
        console.log(key)
    }

    componentDidMount(){
        var ID = this.props.id;
        SecureGet({
