import React, { Component } from 'react';
import { Table, Input, Button, Typography, Divider, Modal,Icon, Popconfirm } from 'antd';
import Highlighter from 'react-highlight-words';
import { connect } from 'react-redux';
import { 
    ChangeChapterSearchText,
    ChangeChapterTableData,
    ChangeChapterModalState,
    ChangeMaterialTableData,
} from '../../../actions/chapterAction';
import './allchapter.css';
import Alert from '../../../components/common/alert';
import {SecurePost, SecureGet} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import NewChapterForm from './newchapter';
import AllMaterial from '../Materials/allmaterials';
//import App from '../chapterwindow/materials';

class AllChapter extends Component {

  constructor(props){
    super(props);
    this.state={
      chapterId : null,
      chapterModelVisible:false,
      CompanytableLoading : false,
    }
  }

  OpendetailsModal = (id)=>{
    this.setState((previousState,previousProps)=>{
       console.log(id)
      return{
        chapterId:id,
        chapterModelVisible:true
      }
    })
  }
  ClosedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        chapterId:null,
        chapterModelVisible:false
      }
    })
  }
  componentDidMount(){
    this.props.ChangeChapterTableData();
  }


  openModal = (id,mode)=>{
    this.props.ChangeChapterModalState(true, id, mode);
  }

  closeModal = ()=>{
    this.props.ChangeChapterModalState(false, null, 'Добавить');
  }


  deleteChapter = (id)=>{
    SecurePost({
      url : `${apis.DELETE_CHAPTER}`,
      data : {
          _id : id
      }
    }).then((response)=>{
      if(response.data.success){
        Alert('success','Успешно',response.data.message);
        this.props.ChangeChapterTableData();
      }
      else{
        return Alert('warning','Предупреждение!',response.data.message);
      }
    }).catch((error)=>{
      return Alert('error','Ошибка!','Ошибка сервера');
    })
  }

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Поиск
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Отмена
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.props.chapter.ChaptersearchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.props.ChangeChapterSearchText(selectedKeys[0])
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.props.ChangeChapterSearchText('')
      };
      // handleChapterChange =(s)=>{
      //   this.props.ChangeChapterTableData(s);
      // }

    render() {
      const { Title } = Typography;
      console.log('fg',this.props.chapter.chapterTableData.icon1);
      const columns = [
      
        {
          title: 'Наименование',
          dataIndex: 'name',
          key: 'name',
          width: '5%',
          ...this.getColumnSearchProps('name'),
        },
        {
          title: 'Описание',
          dataIndex: 'descr',
          key: 'descr',
          width: '20%',
          ...this.getColumnSearchProps('descr'),
        },    
        // { 
        //   title: 'Достижения',
        //   dataIndex: '_id',
        //   key: '_id',
        //   render: (key) => (
        //     // this.props.chapter.chapterTableData.map((icon,i)=>{
        //     // <img src={icon} style={{width: "60px",height: "60px"}}/>})
        //   ),
          
        //   // key2: 'icon2',
        //   // key3: 'icon3',
        //   // key4: 'icon4',
        //   // render: (key1, key2, key3, key4) => (
  
        //   //   <div>
        //   //     <img src={key1} style={{width: "60px",height: "60px"}}/>
        //   //     <img src={key2} style={{width: "60px",height: "60px"}}/>
        //   //     <img src={key3} style={{width: "60px",height: "60px"}}/>
        //   //     <img src={key4} style={{width: "60px",height: "60px"}}/>
        //   //       {/* <img class="icon-gray"/> 
        //   //       <img class="icon-bronze"/> 
        //   //       <img class="icon-silver"/>  
        //   //   <img class="icon-gold"/> */}
        //   //   </div>
        //   // )
          
        // },
        
        
        {
          title: 'Действие',
          key: '_id',
          dataIndex: '_id',
          width: '25%',
          render: (key) => (
            <span>
              <Button type="primary" shape="circle" icon="edit" onClick={()=>this.openModal(key,'Сохранить')}/>
                <Divider type="vertical" />
                <Popconfirm
                  title="Вы уверены？"
                  cancelText="Нет"
                  okText="Да"
                  onConfirm={()=>{this.deleteChapter(key)}}
                  icon={<Icon type="delete" style={{ color: 'red' }} />}
                >
                  <Button type="danger" shape="circle" icon="delete" />
                </Popconfirm>
                <Divider type="vertical" />
                <Button type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>Материалы</Button>
            </span>
          ),
        },
      ];
        return (
            <div className="admin-table-container">
              <div>
                    <Button type="primary" icon="user-add" style={{marginBottom:'10px'}} onClick={()=>this.openModal(null,'Добавить')}>
                      Добавить
                    </Button> 
              </div>

             
              <div className="register-chapter-form-header">
                <Title level={4} style={{color:'#fff',textAlign:'center'}}>Разделы</Title>
              </div>
              <Table 
                bordered={true} 
                columns={columns} 
                dataSource={this.props.chapter.chapterTableData} 
                size="medium" 
                pagination={{ pageSize: 5 }}
                loading={this.props.chapter.chapterTableLoadingStatus}
                rowKey="_id" 
              ></Table>

              <Modal
                visible={this.props.chapter.ChaptermodalOpened}
                title={false}
                onOk={this.handleOk}
                onCancel={this.closeModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="40%"
                destroyOnClose={true}
                footer={[
                  
                ]}
              >
                <NewChapterForm />
              </Modal>
              
              <Modal
                visible={this.state.chapterModelVisible}
                title="Материалы"
                onCancel={this.ClosedetailsModal}
                style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
                width="70%"
                destroyOnClose={true}
                footer={[]}
              >
              <AllMaterial id={this.state.chapterId} />
              </Modal>
            </div>
        )
    }
}

const mapStateToProps = state => ({
  chapter : state.chapter
});

export default connect(mapStateToProps,{
    ChangeChapterSearchText,
    ChangeChapterTableData,
    ChangeChapterModalState,
    ChangeMaterialTableData,
})(AllChapter);
