import React, { Component, useState } from 'react'
import '../newCompany/newcompany.css';
import {
    Form,
    Input,
    Button,
    Select,
    Icon,
    Upload
} from 'antd';
import {SecurePost} from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import { connect } from 'react-redux';
import { 
    ChangeMaterialConfirmDirty,
    ChangeMaterialModalState,
    ChangeMaterialTableData
} from '../../../actions/chapterAction';
import Alert from '../../../components/common/alert';
import auth from '../../../services/AuthServices';
const { Option } = Select;



class NewMaterial extends Component {

    constructor(props){
        super(props);
        this.state={
            materialDetails : {
                //materialimage: this.props.chapter.materialdetails.slides,
                slides :[
                    {
                        slidebody : null,
                        image :null,
                    },
                ], 
            },
            submitDisabled:false,
        }
    }

    // MaterialImageonChange = (f)=>{
    //     var newslide = this.state.materialDetails.materialimage
    //     if(!f){
    //         newslide=null
    //     }
    //     else{
    //         newslide=`${apis.BASE}/${f.link}`
    //     }
    //     this.setState({
    //         submitDisabled:false
    //     })
    //     if(newslide==='undefined' || newslide===undefined || newslide===null || newslide==='null') {
    //         //(newOptions[i].body==='undefined' || newOptions[i].body===undefined || newOptions[i].body==='null' || newOptions[i].body==='' || newOptions[i].body===null)){
    //         newslide=this.state.materialDetails.materialimage;    
    //     }
    //     this.setState((ps,pp)=>{
    //         return({
    //             materialDetails:{
    //                 ...ps.materialDetails,
    //                 materialimage:newslide
    //             }
    //         })
    //     })
    // }


    MaterialImageonChange = (f,i)=>{
        var newSlides = [...this.state.materialDetails.slides]
        if(!f){
            delete newSlides[i].image
            newSlides[i].image=null
        }
        else{
            newSlides[i]={
                ...this.state.materialDetails.slides[i],
                image :`${apis.BASE}/${f.link}`
            }
        }
        this.setState({
            submitDisabled:false
        })
        if((newSlides[i].image==='undefined' || newSlides[i].image===undefined || newSlides[i].image===null || newSlides[i].image==='null') && 
            (newSlides[i].slidebody==='undefined' || newSlides[i].slidebody===undefined || newSlides[i].slidebody==='null' || newSlides[i].slidebody==='' || newSlides[i].slidebody===null)){
                newSlides[i]={
                    ...this.state.materialDetails.slides[i],
                }
        }
        this.setState((ps,pp)=>{
            return({
                materialDetails:{
                    ...ps.materialDetails,
                    slides:newSlides
                }
            })
        })
    }


    
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Полученные значения формы: ', values);
                
                    SecurePost({
                        url : `${apis.CREATE_MATERIAL}`,
                        data : {
                            _id : this.props.chapter.materialId,
                            topic :values.topic,
                            slides : this.state.materialDetails.materialimage,
                        }
                    }).then((response)=>{
                        if(response.data.success){
                            this.props.ChangeMaterialModalState(false,null,'Регистрация');
                            Alert('success','Успешно',response.data.message);
                            this.props.ChangeMaterialTableData();
                        }
                        else{
                            console.log(response.data);
                            this.props.ChangeMaterialModalState(false,null,'Регистрация');
                            return Alert('warning','Предупреждение!',response.data.message);
                        }
                    }).catch((error)=>{
                        this.props.ChangeMaterialModalState(false,null,'Регистрация');
                        return Alert('error','Ошибка!','Ошибка сервера');
                    })
                
            }
        });
    };

    changeqImage = (f)=>{
        this.setState((ps,pp)=>{
            return({
                materialDetails:{
                    ...ps.materialDetails,
                    materialimage:(f.link ?`${apis.BASE}/${f.link}`:null)
                },
                submitDisabled:false
            })
        })
    }
    upl=()=>{
        this.setState({
            submitDisabled: false
        })
    }


    render() {
        const { getFieldDecorator } = this.props.form;
        var MaterialImageprops={
            name: 'file',
            action: `${apis.BASE}${apis.FILE_UPLOAD}?Token=${auth.retriveToken()}`,
            listType: 'picture',
        }
        
        return (
            <div className="register-company-form">
                <div className="register-company-form-body">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Item label="Название" hasFeedback className="input-admin-company">
                            {getFieldDecorator('topic', {
                                initialValue : this.props.chapter.materialdetails.topic,
                                rules: [{ required: true, message: 'Пожалуйста, введите название!', whitespace: true }],
                            })(<Input />)}
                        </Form.Item>
                        
                        <Form.Item label="Material">
                        <Upload {...MaterialImageprops} beforeUpload={this.upl} onRemove={(f)=>this.MaterialImageonChange(null)} onSuccess={(f)=>this.MaterialImageonChange(f)}>
                                {getFieldDecorator('slides', {
                                    initialValue : this.props.chapter.materialdetails.slides,
                                    rules: [{ required: false, message: 'Пожалуйста, загрузите логотип вашей компании!', whitespace: true }],
                                })(<img src={this.props.chapter.materialdetails.slides} style={{width: "60px",height: "60px",}}/>)
                                }
                                
                                <Button style={{marginLeft: '100px'}}>
                                    <Icon type="upload" /> Загрузить
                                </Button>
                                
                            </Upload>
                        </Form.Item>
                        
                        <Form.Item>
                            <Button type="primary" htmlType="submit" block>
                                {this.props.chapter.Materialmode}
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    chapter : state.chapter
});



const NewMaterialForm = Form.create({ name: 'register' })(NewMaterial);

export default connect(mapStateToProps,{
    ChangeMaterialConfirmDirty,
    ChangeMaterialModalState,
    ChangeMaterialTableData
})(NewMaterialForm);
