import React, { Component } from 'react';
import { Table, Button, Divider, Modal } from 'antd';
import { connect } from 'react-redux';
import { 
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState
} from '../../../actions/chapterAction';
import { 
  ChangeQuestionSearchText,
  ChangeQuestionTableData,
  ChangeQuestionModalState
} from '../../../actions/trainerAction';
import './chapters.css';
import { SecureGet } from '../../../services/axiosCall';
import apis from '../../../services/Apis';
import SliderShow from './slider';
import Test from '../Testing/test';

class Materials extends Component {
  constructor(props){
    super(props);
    this.state={
      materialId : null,
      slideModelVisible : false,
      testModelVisible : false,
      details: null,
      loading:true
    }
  }

  OpendetailsModal = (id)=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        slideModelVisible:true
      }
    })
  }

  ClosedetailsModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        slideModelVisible:false
      }
    })
  }

  OpentestModal = (id)=>{
    console.log('POIU',id)
    this.setState((previousState,previousProps)=>{
      return{
        materialId:id,
        testModelVisible:true
      }
    })
  }

  ClosetestModal = ()=>{
    this.setState((previousState,previousProps)=>{
      return{
        materialId:null,
        testModelVisible:false
      }
    })
  }


  componentDidMount(){
    var ID = this.props.id;
    this.props.ChangeMaterialTableData(ID);
    SecureGet({
       url: `${apis.GET_SINGLE_CHAPTER_DETAILS}/${ID}`,
    }).then((response)=>{
      console.log(response.data.data[0]);
      this.setState({
        details : response.data.data[0],
        loading : false
      })
    }).catch((error)=>{
      console.log(error);
    })
    
  }


  render() {
    // const { Title } = Typography;
    const columns = [
      {
        title: '№',
        dataIndex: 'num',
        key: 'num',
        width: '5%',
      },   
      {
        title: 'Наименование',
        dataIndex: 'topic',
        key: 'topic',
        width: '5%',
      },   
      {
        title: 'Действие',
        key: '_id',
        dataIndex: '_id',
        width: '25%',
        render: (key) => (
          <span>
            <Button type="primary" icon="" onClick={()=>this.OpendetailsModal(key)}>Уроки</Button>
            <Divider type="vertical" />
            <Button type="primary" icon="" onClick={()=>this.OpentestModal(key)}>Тест</Button>
          </span>
        ),
      },
    ];
    return(
      <div className="admin-table-container">
        <Table 
          bordered={true} 
          columns={columns} 
          dataSource={this.props.chapter.materialTableData} 
          size="medium" 
          pagination={{ pageSize: 5 }}
          loading={this.props.chapter.materialTableLoadingStatus}
          rowKey="_id" 
        />

        <Modal
          visible={this.state.slideModelVisible}
          title="Уроки"
          onCancel={this.ClosedetailsModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="85%"
          destroyOnClose={true}
          footer={[]}
        >
          <SliderShow id={this.state.materialId} />
        </Modal>

        <Modal
          visible={this.state.testModelVisible}
          title="Тест"
          onCancel={this.ClosetestModal}
          style={{top :'20px',padding:'0px',backgroundColor:'rgb(155,175,190)'}}
          width="80%"
          destroyOnClose={true}
          footer={[]}
        >
          <Test id={this.state.materialId} details={this.props.trainer.QuestionTableData} />
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  chapter : state.chapter,
  trainer : state.trainer
});

export default connect(mapStateToProps,{
    ChangeMaterialSearchText,
    ChangeMaterialTableData,
    ChangeMaterialModalState,
})(Materials);
